import { createRouter, createWebHistory } from "vue-router";
import SideMenu from "@/layout/BaseLayout.vue";
import Button from "@/components/Button/ButtonsLayout.vue";
import DropDown from "@/components/Dropdown/DropdownLayout.vue";
import Modal from "@/components/Modal/DropdownLayout.vue";
const routes = [
  {
    path: "/",
    component: SideMenu,
    children: [
      {
        path: "/button",
        component: Button,
        name: "Button",
      },
      {
        path: "/dropDown",
        component: DropDown,
        name: "DropDown",
      },
      {
        path: "/modal",
        component: Modal,
        name: "Modal",
      },
    ],
  },
];
const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
  scrollBehavior(to, from, savedPosition) {
    return savedPosition || { left: 0, top: 0 };
  },
});

export default router;
