export default {
  props: {
    value: {
      type: Array,
      default: () => []
    },
    id: {
      type: Number
    }
  },

  data() {
    return {
      checked: false
    };
  },

  watch: {
    value: {
      handler() {
        this.checked = this.value.includes(this.id);
      },

      deep: true
    }
  },

  created() {
    this.checked = this.value?.includes(this.id);
  },

  methods: {
    onChange() {
      this.$emit("change", this.id);
    }

  }
};
